const bcrypt = require('bcrypt')
const { getConnection } = require('./../database/database')
const { tokenSign } = require('../middlewares/generateToken')
const { emailVerification, getTemplateVerification } = require('../helpers/verifyEmail')

const userLogin = async (req = request, res = response) => {
    const { email, password } = req.body;
        //Tomamos los datos ingresados por el usuario y lo enviamos en una consulta
        const connection = await getConnection();
        const result = await connection.query('SELECT * FROM users WHERE email = ?', email) || null;
        //Si el usuario no existe enviamos un mensaje
        if (result == null) {
            res.status(404).json({
                ok: false,
                msg: 'El usuario no existe'
            })
        } else {
        //El usuario existe, verificamos si la cuenta esta verificada o no.
        const verify = result[0].verifyAccount
        //si la verificacion es igual a FALSE le enviamos un mensaje recordando que debe verificar
        if (verify == 0) {
            res.status(404).json({
                ok: false,
                msg: 'Debes confirmar tu cuenta antes de poder ingresar, revisa tu casilla de correo.'
            })
        } else {
            //el usuario esta verificado, verificamos si la contrase単a es correcta
            const hashPassword = result[0].password
            const check = await bcrypt.compare(password, hashPassword);
            //Enviamos un mensaje si esta es incorrecta
            if (!check) {
                res.satatus(401).json({
                    ok: false,
                    msg: 'La contrase単a es incorrecta'
                })
            }
        }
            //Sabiendo que la contrase単a es correcta, creamos un token para el usuario con sus datos.
            const id_user = result[0].id_user
            const role = result[0].role
            const permissions = result[0].permissions
            const token = await tokenSign(id_user, role, permissions)
            res.status(200).json({
                ok: true,
                token: token
            })
        }
}
const userRegister = async (req, res) => {
    const date_joined = new Date()
    //
    const { user, email, password } = req.body
    //Creamos un array con la informacion del usuario la cual fue ingresada por el mismo y ademas tomamos el dia que lo hizo.
    const usuario = {
        user,
        email,
        password,
        date_joined
    }
    //encriptamos la password:
    const salt = bcrypt.genSaltSync();
    usuario.password = bcrypt.hashSync(password, salt);
    const connection = await getConnection();
    const result = connection.query('SELECT email FROM users WHERE email = ?', email) || null;
    //Revisamos si el usuario esta creado, si lo esta enviamos un error y un mensaje
    if (result == null) {
        res.status(404).json({
            ok: false,
            msg: 'El correo ya se encuentra registrado'
        })
    } else {
        //Si no esta creado lo almacenamos en la base
        const createUser = connection.query('INSERT INTO users SET ?', [usuario])
        const emailverify = usuario.email
        const user = usuario.user
        //Tomamos los datos por default (cuenta no verificada)
        const detailsAccount = await connection.query('SELECT * FROM users WHERE email = ?', [emailverify]);
        const id_user = detailsAccount[0].id_user
        const role = detailsAccount[0].role
        const permissions = detailsAccount[0].permissions
        //Creamos un token con la informacion de la cuenta
        const token = await tokenSign(id_user, role, permissions)
        const template = getTemplateVerification(token, user)
        //Enviamos el email y el token a la funcion de enviar email para verificar
        emailVerification(emailverify, template)
        res.status(202).json({
            ok: true,
            msg: 'Usuario creado con exito'
        })
    }
}
module.exports = {
    userLogin,
    userRegister
}

