const { getConnection } = require("../database/database")
const cloudinary = require('../utils/cloudinary')





const addProduct = async (req = request, res = response) => {
    const {price, brand, discount, description, status, name, category, advertiser_identifier, keywords} = req.body
    const ahora = new Date();
    const date_create = ahora;
    try {
        const uploader = async (path) => await cloudinary.uploads(path, 'Images', {height: 200, width: 200, crop: "crop"});
        if (req.method === 'POST') {
            var urls = []
            const files = req.files;
            for (const file of files) {
                const { path } = file;
                console.log(file)
                const newPath = await uploader(path);
                urls.push(newPath)
            }
            var urlwithastag = []
            // console.log(urls.res)
            for (let x = 0; x < urls.length; x++) {
                urlwithastag.push(`${urls[x].res}#`)
            }
            const urlwithcoma = urlwithastag.join()
            const image =  urlwithcoma.replace(/,/g,'')
            //Creamos la lista ordenada de el producto con la informacion ingresada por el administrador
            const newProduct = {
                name,
                description,
                brand,
                price,
                category,
                stock,
                date_create,
                image,
                advertiser_identifier,
                status,
                keywords,
                discount
            };
            const connection = await getConnection();
            const sql = ('INSERT INTO products SET ?')
            const result = await connection.query(sql, [newProduct]);
            if (result.length > 0) {
                res.status(404).json({
                    ok: false,
                    msg: 'Ocurrio un error al crear el producto'
                })
            }
            else {
                res.status(200).json({
                    ok: true,
                    msg: 'Imagen subida con exito',
                    data: urls
                });
            }
        } else {
            res.status(405).json({
                err: `${req.method} metodo no`
            })
        }
    } catch (error) {
        res.status(500).json({
            ok: false,
            msg: error.message
        })
    }
}



const getProductsCard = async (req = request, res = response) => {
    try {
        const connection = await getConnection()
        const result = await connection.query('SELECT id_shoes, title,price,id_brand,dataurl, id_shoes FROM products')
        if (!result) {
            res.status(404).json({
                ok: false,
                msg: 'Error al cargar las zapatillas'
            });
        }

        res.json(result)
    } catch (error) {
        return res.status(500).json({
            ok: false,
            msg: "No se pudo conectar al servidor getCards"
        });
    }
}





const getProduct = async (req = request, res = response) => {
    const {id_shoes} = req.params;
    try {
        const connection = await getConnection()
        const result = await connection.query('SELECT * FROM shoes WHERE id_shoes = ?', [id_shoes])
        const id_brand = result[0].id_brand
        const brand = await connection.query('SELECT name_brand FROM brands WHERE id_brand = ?', [id_brand])
        result[0].id_brand = brand
        var str = result[0].dataurl
        const url = str.split('#')
        result[0].dataurl = url
        if (!result) {
            res.status(404).json({
                ok: false,
                msg: "Ocurrio un error al intentar obtener estas zapatillas"
            })
        }
        res.json(result[0])
    } catch (error) {
        res.status(500).json({
            ok: false,
            msg: "No se pudo conectar al servidor GetProduct"
        });
    }
}



const getProducts = async (req = request, res = response) => {
    try {
        const connection = await getConnection()
        const result = await connection.query('SELECT * FROM shoes')
        if (!result) {
            res.status(404).json({
                ok: false,
                msg: "Ocurrio un error al intentar obtener estas zapatillas"
            })
        }
        var str = result[0].dataurl
        const url = str.split('#',1)
        result[0].dataurl = url
        res.json(result)
    } catch (error) {
        res.status(500).json({
            ok: false,
            msg: "No se pudo conectar al servidor GetProducts"
        });
    }
}


const updateProduct = async (req = request, res = response) => {
    const { id_shoes } = req.params;
    const { title, price, image, model, id_brand, gender, id_size, discount, description } = req.body
    try {
        const connection = await getConnection()
        const result = await connection.query('UPDATE shoes SET title = ?, price = ?, image = ?, model = ?, id_brand = ?, gender = ?, id_size = ?, discount = ?, description = ? WHERE id_shoes = ?'[title, price, image, model, id_brand, gender, id_size, discount, description, id_shoes])
        if (!result) {
            res.status(404).json({
                ok: false,
                msg: "Ocurrio al actualizar el producto"
            })
        } else {
            res.status(200).json({
                ok: true,
                msg: 'todo salio bien, producto actualizado'
            })
        }
    } catch (error) {
        return res.status(500).json({
            ok: false,
            msg: "No se pudo conectar al servidor updateProduct"
        })
    }
}


const delProduct = async (req = request, res = response) => {
    const { id_shoes } = req.params;
    console.log(id_shoes)
    try {
        const connection = await getConnection();
        const result = await connection.query('DELETE FROM shoes WHERE id_shoes = ?',[id_shoes]);
        console.log(result)
        if (!result) {
            res.status(404).json({
                ok: false,
                msg: 'El producto no se encontro o ya fue eliminado.'
            })
        }
        else {
            res.status(200).json({
                ok: true,
                msg: 'Producto eliminado con exito!'
            })
        }
    } catch {
        return res.status(500).json({
            ok: false,
            msg: 'No se pudo conectar al servidor deleteProduct'
        })
    }
}
module.exports = {
    addProduct,
    getProduct,
    updateProduct,
    delProduct,
    getProductsCard,
    getProducts
}

